// El componente App es el padre de:
// - Cabecera
// - Listado
// ESTADO: App debe manejar en su estado un número para contabilizar el total de elementos comprados.
// MÉTODOS: App debe tener un método para aumentar este número y que pueda ser ejecutado por su nieto Item.
// PROPS: App deberá pasar por props lo necesario a sus componenetes internos.

function App() {
  const [cantidadProductos, setCantidadProductos] = useState([]);
  function acumulador(event){
    event.preventDefault();
    const v1 = parseInt(event.target.valor1.value);
    const v2 = parseInt(event.target.valor2.value);
    const suma = v1+ v2;
    const nuevo = {
      resultado: suma,
      valor1: v1,
      valor2: v2
    }
    setCantidadProductos([nuevo, ...cantidadProductos])
    event.target.valor1.value="";
    event.target.valor2.value="";
  }

  return (
    <div className="App">
      <Cabecera onAcumulador={acumulador}/>
      <Listado resultado={cantidadProductos}/>
    </div>
  );
  }

export default App;
