import React from 'react'
import DataJson from './data.json';


// Debemos importar el listado de objetos del archivo JSON para usarlos como array a la hora de crear cada Item.
// El componente Listado es el padre de:
// - varios Item
// ESTADO: Listado no necesita manejar un estado.
// MÉTODOS: Listado no requiere de métodos.
// PROPS: Listado recibe el método para aumentar el estado de App y se lo pasa a cada uno de sus hijos.
const data=DataJson;
export default function Listado(props) {
  state = {
    producto: data,
    //pokemon: [pokedex[0].name, pokedex[0].id, pokedex[0].type],
    producto: [this.id, this.producto.nombre, this.producto.descripcion,this.stock],
  };
  return (
    <div className='container'>{
      props.data.map((producto) => {
          <li key={producto.id}>
            Id: {producto.id} - Correspondiente al pproducto {producto.nombre}{' '}
            {producto.stock} stock
          </li>
        })}
    </div>
  );
        }
